import React from 'react';
import './App.css';
import Filter from './components/Filter'
import RenderDog from './components/RenderDog'

class App extends React.Component {

  constructor() {
    super()

    this.state = {
      dogs: [],
      selectedDog: undefined,
      goodDogFilter: false
    }
  }

  selectDog = id => {
    console.log(this.state.dogs.filter(dog => dog.id === id))
    this.setState({
      selectedDog: this.state.dogs.filter(dog => dog.id === id)[0]
    })
  }

  filterGoodDogs = () => {
    this.setState(prevState => {
      console.log()
      return {
        goodDogFilter: prevState.goodDogFilter ? false : true
      }
    })
  }

  render() {
    return (
      <div className="App">
        <div id="filter-div">
        <button id="good-dog-filter" onClick={this.filterGoodDogs}>filter good dogs</button>
        </div>
        <div id="dog-bar">
          {this.state.dogs.map(dog => <span key={dog.id} onClick={() => this.selectDog(dog.id)}>{dog.name}</span>)}
        </div>
        <div id="dog-summary-container">
          <h1>DOGGO:</h1>
          <div id="dog-info">
            {
            this.state.goodDogFilter ?
            this.state.selectedDog ? <RenderDog dog={this.state.selectedDog.filter(dog => dog.isGoodDog)} /> : null
            :
            this.state.selectedDog ? <RenderDog dog={this.state.selectedDog} /> : null
          }
          </div>
        </div>
      </div>
    );
  }

  componentDidMount() {
    fetch('http://localhost:3001/pups')
    .then(resp => resp.json())
    .then(dogs => {
      this.setState({dogs})
    })
  }
}

export default App;
